mapper.update:

		update mem_status set mem_st = 'online'
  		where mem_id = #{memId}
  		 and mem_st <> 'online'


		update mem_status set mem_st = 'offline'
  		where mem_id = #{memId}
  		 and mem_st <> 'offline'

mapper.select:

select co_url, co_name 
from company
where co_url = #{coUrl}

mapper.update:
update member 
set name = #{name}
where mem_id = #{memId}

update member 
set wkpo = #{wkpo}
where mem_id = #{memId}

update member 
set pers_Tel = #{persTel}
where mem_id = #{memId}

update member 
set co_tel = #{coTel}
where mem_id = #{memId}

update member 
set dept = #{dept}
where mem_id = #{memId}
