mapper.select:
select a.mem_id, a.name, a.pwd, a.email, a.mem_perm, b.mem_st
from member a, mem_status b
where a.mem_id = b.mem_id and a.email = #{email} and a.pwd = ${pwd}

mapper.update:
		update mem_status set mem_st = 'online'
  		where a.email = #{email}
  		 and mem_st <> 'online'

mapper.select:
select co_url, co_name 
from company
where co_url = #{coUrl}
